sect2.programlisting:
SUMOTime execute() {
   size_t step1 = myTLLogic->getStepNo(); // get the current phase index
   SUMOTime next = myTLLogic->trySwitch(); // try to switch, get next duration
   size_t step2 = myTLLogic->getStepNo(); // get the phase index after possible switch
   if(step1!=step2) { // has the phase changed?
      myTLLogic->onSwitch(); // yes -> do some further actions (see below)
      myTLLogic->setLinkPriorities(); // inform the links about current state
   }
   return next; // return period after which the tls shall be tried to switch again
}

SUMOTime
MSSimpleTrafficLightLogic::trySwitch()
{
    // increment the index
    myStep++;
    // if the last phase was reached ...
    if(myStep==myPhases.size()) {
        // ... set the index to the first phase
        myStep = 0;
    }
    // return offset to the next switch
    assert(myPhases.size()>myStep);
    return myPhases[myStep]->duration;
}

