/// builds a busstop
MSBusStop *parseAndBuildBusStop(MSNet &net,
    const Attributes &attrs, const std::string &base,
    const NLHandler &helper);

MSBusStop *
NLTriggerBuilder::parseAndBuildBusStop(MSNet &net,
                                       const Attributes &attrs,
                                       const std::string &base,
                                       const NLHandler &helper)
{
    string id = helper.getString(attrs, SUMO_ATTR_ID);
    string objectid = helper.getString(attrs, SUMO_ATTR_OBJECTID);
    MSLane *lane = MSLane::dictionary(objectid);
    if(lane==0) {
        MsgHandler::getErrorInstance()->inform(
            string("The lane to use within MSEmitter '")
            + id + string("' is not known."));
        throw ProcessError();
    }
    SUMOReal frompos = helper.getFloat(attrs, SUMO_ATTR_FROM);
    SUMOReal topos = helper.getFloat(attrs, SUMO_ATTR_TO);
    if(frompos<0) {
        frompos = lane->length() + frompos;
    }
    if(topos<0) {
        topos = lane->length() + topos;
    }
    return buildBusStop(net, id, lane, frompos, topos, file);
}

virtual MSBusStop* buildBusStop(MSNet &net,
    const std::string &id, MSLane *lane,
    SUMOReal frompos, SUMOReal topos);

MSBusStop*
NLTriggerBuilder::buildBusStop(MSNet &net, const std::string &id,
                               MSLane *lane,
                               SUMOReal frompos, SUMOReal topos)
{
    return new MSBusStop(id, std::vector<std::string>(), lane, frompos, toPos);
}

